package cn.com.bohui.bohuifin.service.access_log;/** * 表名：t_access_log */import javax.annotation.Resource;import org.springframework.stereotype.Service;import cn.com.bohui.bohuifin.mapper.AccessLogMapper;import java.util.List;import java.util.ArrayList;import cn.com.bohui.bohuifin.bean.Page;import cn.com.bohui.bohuifin.bean.AccessLogBean;@Servicepublic class AccessLogService {	@Resource	private AccessLogMapper accessLogMapper;	public void saveAccessLog(AccessLogBean accessLogBean) throws Exception {		accessLogMapper.saveAccessLog(accessLogBean);	}	public void updateAccessLog(AccessLogBean accessLogBean) throws Exception {		accessLogMapper.updateAccessLog(accessLogBean);	}	public List<AccessLogBean> listAccessLogByPage(Page<AccessLogBean> page) throws Exception {		return accessLogMapper.listAccessLogByPage(page);	}	public void removeAccessLogById(AccessLogBean accessLogBean) throws Exception {		accessLogMapper.removeAccessLogById(accessLogBean);	}}