package cn.com.bohui.bohuifin.service.account_record;/** * 表名：t_account_record */import javax.annotation.Resource;import org.springframework.stereotype.Service;import cn.com.bohui.bohuifin.mapper.AccountRecordMapper;import java.util.List;import java.util.ArrayList;import cn.com.bohui.bohuifin.bean.Page;import cn.com.bohui.bohuifin.bean.AccountRecordBean;@Servicepublic class AccountRecordService {	@Resource	private AccountRecordMapper accountRecordMapper;	public void saveAccountRecord(AccountRecordBean accountRecordBean) throws Exception {		accountRecordMapper.saveAccountRecord(accountRecordBean);	}	public void updateAccountRecord(AccountRecordBean accountRecordBean) throws Exception {		accountRecordMapper.updateAccountRecord(accountRecordBean);	}	public List<AccountRecordBean> listAccountRecordByPage(Page<AccountRecordBean> page) throws Exception {		return accountRecordMapper.listAccountRecordByPage(page);	}	public void removeAccountRecordById(AccountRecordBean accountRecordBean) throws Exception {		accountRecordMapper.removeAccountRecordById(accountRecordBean);	}}