package cn.com.bohui.bohuifin.service.authority;/** * 表名：t_authority */import javax.annotation.Resource;import org.springframework.stereotype.Service;import cn.com.bohui.bohuifin.mapper.AuthorityMapper;import java.util.List;import java.util.ArrayList;import java.util.Map;import cn.com.bohui.bohuifin.bean.Page;import cn.com.bohui.bohuifin.bean.AuthorityBean;@Servicepublic class AuthorityService {    @Resource    private AuthorityMapper authorityMapper;    public void saveAuthority(AuthorityBean authorityBean) throws Exception {        authorityMapper.saveAuthority(authorityBean);    }    public void updateAuthority(AuthorityBean authorityBean) throws Exception {        authorityMapper.updateAuthority(authorityBean);    }    public List<AuthorityBean> listAuthorityByPage(Page<AuthorityBean> page) throws Exception {        return authorityMapper.listAuthorityByPage(page);    }    public List<AuthorityBean> listAuthorityByAuthorityGroupId(Map<String, Object> params) throws Exception {        return authorityMapper.listAuthorityByAuthorityGroupId(params);    }    public void removeAuthorityById(AuthorityBean authorityBean) throws Exception {        authorityMapper.removeAuthorityById(authorityBean);    }}