package cn.com.bohui.bohuifin.service.authority_group;/** * 表名：t_authority_group */import javax.annotation.Resource;import cn.com.bohui.bohuifin.consts.SystemConst;import org.springframework.stereotype.Service;import cn.com.bohui.bohuifin.mapper.AuthorityGroupMapper;import java.util.List;import java.util.ArrayList;import cn.com.bohui.bohuifin.bean.Page;import cn.com.bohui.bohuifin.bean.AuthorityGroupBean;@Servicepublic class AuthorityGroupService {    @Resource    private AuthorityGroupMapper authorityGroupMapper;    public List<AuthorityGroupBean> listAllAUthorityGroup() throws Exception {        AuthorityGroupBean bean = new AuthorityGroupBean();        bean.setState(SystemConst.STATE_DEFAULT);        return authorityGroupMapper.listAllAUthorityGroup(bean);    }    public void saveAuthorityGroup(AuthorityGroupBean authorityGroupBean) throws Exception {        authorityGroupMapper.saveAuthorityGroup(authorityGroupBean);    }    public void updateAuthorityGroup(AuthorityGroupBean authorityGroupBean) throws Exception {        authorityGroupMapper.updateAuthorityGroup(authorityGroupBean);    }    public List<AuthorityGroupBean> listAuthorityGroupByPage(Page<AuthorityGroupBean> page) throws Exception {        return authorityGroupMapper.listAuthorityGroupByPage(page);    }    public void removeAuthorityGroupById(AuthorityGroupBean authorityGroupBean) throws Exception {        authorityGroupMapper.removeAuthorityGroupById(authorityGroupBean);    }}