package cn.com.bohui.bohuifin.service.dept;/** * 表名：t_dept */import javax.annotation.Resource;import org.springframework.stereotype.Service;import cn.com.bohui.bohuifin.mapper.DeptMapper;import java.util.List;import java.util.ArrayList;import cn.com.bohui.bohuifin.bean.Page;import cn.com.bohui.bohuifin.bean.DeptBean;@Servicepublic class DeptService {	@Resource	private DeptMapper deptMapper;	public void saveDept(DeptBean deptBean) throws Exception {		deptMapper.saveDept(deptBean);	}	public void updateDept(DeptBean deptBean) throws Exception {		deptMapper.updateDept(deptBean);	}	public List<DeptBean> listDeptByPage(Page<DeptBean> page) throws Exception {		return deptMapper.listDeptByPage(page);	}	public void removeDeptById(DeptBean deptBean) throws Exception {		deptMapper.removeDeptById(deptBean);	}}