package cn.com.bohui.bohuifin.service.manager;/** * 表名：t_manager */import cn.com.bohui.bohuifin.bean.ManagerBean;import cn.com.bohui.bohuifin.bean.Page;import cn.com.bohui.bohuifin.bean.vo.ManagerVo;import cn.com.bohui.bohuifin.consts.ParamConst;import cn.com.bohui.bohuifin.consts.SystemConst;import cn.com.bohui.bohuifin.mapper.ManagerMapper;import cn.com.bohui.bohuifin.util.LogicUtil;import org.springframework.stereotype.Service;import javax.annotation.Resource;import javax.servlet.http.HttpSession;import java.util.List;import java.util.Map;@Servicepublic class ManagerService {    @Resource    private ManagerMapper managerMapper;    public void saveManager(ManagerBean managerBean) throws Exception {        managerMapper.saveManager(managerBean);    }    public void updatePwd(ManagerBean managerBean) throws Exception {        managerMapper.updatePwd(managerBean);    }    public void updateManager(ManagerBean managerBean) throws Exception {        managerMapper.updateManager(managerBean);    }    public List<ManagerBean> listManagerByPage(Page<ManagerBean> page) throws Exception {        return managerMapper.listManagerByPage(page);    }    public List<ManagerBean> listAllManagers(Map<String, Object> params) throws Exception {        return managerMapper.listAllManagers(params);    }    public ManagerBean findManagerById(String managerId) throws Exception {        return managerMapper.findManagerById(managerId);    }    public void removeManagerById(ManagerBean managerBean) throws Exception {        managerMapper.removeManagerById(managerBean);    }    public void removeManagersById(ManagerVo managerVo) throws Exception {        managerMapper.removeManagersById(managerVo);    }    public boolean isLogin(ManagerBean managerBean, HttpSession session) throws Exception {        managerBean.setState(SystemConst.STATE_DEFAULT);        ManagerBean login = managerMapper.getManagerBean(managerBean);        return LogicUtil.getInstance().checkIsLogin(login, managerBean, ParamConst.MANAGER_LOGIN, session);    }    public boolean checkManagerExist(String managerId, String managerName, int authorityGroupId) throws Exception {        ManagerBean managerBean = new ManagerBean();        managerBean.setManagerId(managerId);        managerBean.setManagerName(managerName);        managerBean.setAuthorityGroupId(authorityGroupId);        managerBean.setState(SystemConst.STATE_DEFAULT);        ManagerBean result = managerMapper.getManagerBean(managerBean);        if (result != null) {            return true;        } else {            return false;        }    }}