package cn.com.bohui.bohuifin.service.op_log;/** * 表名：t_op_log */import javax.annotation.Resource;import org.springframework.stereotype.Service;import cn.com.bohui.bohuifin.mapper.OpLogMapper;import java.util.List;import java.util.ArrayList;import cn.com.bohui.bohuifin.bean.Page;import cn.com.bohui.bohuifin.bean.OpLogBean;@Servicepublic class OpLogService {	@Resource	private OpLogMapper opLogMapper;	public void saveOpLog(OpLogBean opLogBean) throws Exception {		opLogMapper.saveOpLog(opLogBean);	}	public void updateOpLog(OpLogBean opLogBean) throws Exception {		opLogMapper.updateOpLog(opLogBean);	}	public List<OpLogBean> listOpLogByPage(Page<OpLogBean> page) throws Exception {		return opLogMapper.listOpLogByPage(page);	}	public void removeOpLogById(OpLogBean opLogBean) throws Exception {		opLogMapper.removeOpLogById(opLogBean);	}}