package cn.com.bohui.bohuifin.service.product_oper_log;/** * 表名：t_product_oper_log */import javax.annotation.Resource;import org.springframework.stereotype.Service;import cn.com.bohui.bohuifin.mapper.ProductOperLogMapper;import java.util.List;import java.util.ArrayList;import cn.com.bohui.bohuifin.bean.Page;import cn.com.bohui.bohuifin.bean.ProductOperLogBean;@Servicepublic class ProductOperLogService {	@Resource	private ProductOperLogMapper productOperLogMapper;	public void saveProductOperLog(ProductOperLogBean productOperLogBean) throws Exception {		productOperLogMapper.saveProductOperLog(productOperLogBean);	}	public void updateProductOperLog(ProductOperLogBean productOperLogBean) throws Exception {		productOperLogMapper.updateProductOperLog(productOperLogBean);	}	public List<ProductOperLogBean> listProductOperLogByPage(Page<ProductOperLogBean> page) throws Exception {		return productOperLogMapper.listProductOperLogByPage(page);	}	public void removeProductOperLogById(ProductOperLogBean productOperLogBean) throws Exception {		productOperLogMapper.removeProductOperLogById(productOperLogBean);	}}