package cn.com.bohui.bohuifin.service.product_product_tag;/** * 表名：t_product_product_tag */import javax.annotation.Resource;import cn.com.bohui.bohuifin.bean.vo.ProductProductTagVo;import org.springframework.stereotype.Service;import cn.com.bohui.bohuifin.mapper.ProductProductTagMapper;import java.util.List;import java.util.ArrayList;import java.util.Map;import cn.com.bohui.bohuifin.bean.Page;import cn.com.bohui.bohuifin.bean.ProductProductTagBean;@Servicepublic class ProductProductTagService {    @Resource    private ProductProductTagMapper productProductTagMapper;    public void saveProductProductTag(ProductProductTagBean productProductTagBean) throws Exception {        productProductTagMapper.saveProductProductTag(productProductTagBean);    }    public void updateProductProductTag(ProductProductTagBean productProductTagBean) throws Exception {        productProductTagMapper.updateProductProductTag(productProductTagBean);    }    public List<ProductProductTagBean> listProductProductTagByPage(Page<ProductProductTagBean> page) throws Exception {        return productProductTagMapper.listProductProductTagByPage(page);    }    public void removeProductProductTagByProductIdAndTagName(ProductProductTagVo productProductTagVo) throws Exception {        productProductTagMapper.removeProductProductTagByProductIdAndTagName(productProductTagVo);    }    public void removeProductProductTagById(ProductProductTagBean productProductTagBean) throws Exception {        productProductTagMapper.removeProductProductTagById(productProductTagBean);    }}