package cn.com.bohui.bohuifin.service.product_type;/** * 表名：t_product_type */import javax.annotation.Resource;import org.springframework.stereotype.Service;import cn.com.bohui.bohuifin.mapper.ProductTypeMapper;import java.util.List;import java.util.ArrayList;import java.util.Map;import cn.com.bohui.bohuifin.bean.Page;import cn.com.bohui.bohuifin.bean.ProductTypeBean;@Servicepublic class ProductTypeService {    @Resource    private ProductTypeMapper productTypeMapper;    public void saveProductType(ProductTypeBean productTypeBean) throws Exception {        productTypeMapper.saveProductType(productTypeBean);    }    public void updateProductType(ProductTypeBean productTypeBean) throws Exception {        productTypeMapper.updateProductType(productTypeBean);    }    public List<ProductTypeBean> listProductTypeByPage(Page<ProductTypeBean> page) throws Exception {        return productTypeMapper.listProductTypeByPage(page);    }    public List<ProductTypeBean> listAllProducts(Map<String, Object> params) throws Exception {        return productTypeMapper.listAllProducts(params);    }    public void removeProductTypeById(ProductTypeBean productTypeBean) throws Exception {        productTypeMapper.removeProductTypeById(productTypeBean);    }}