package cn.com.bohui.bohuifin.service.role;/** * 表名：t_role */import javax.annotation.Resource;import org.springframework.stereotype.Service;import cn.com.bohui.bohuifin.mapper.RoleMapper;import java.util.List;import java.util.ArrayList;import cn.com.bohui.bohuifin.bean.Page;import cn.com.bohui.bohuifin.bean.RoleBean;@Servicepublic class RoleService {	@Resource	private RoleMapper roleMapper;	public void saveRole(RoleBean roleBean) throws Exception {		roleMapper.saveRole(roleBean);	}	public void updateRole(RoleBean roleBean) throws Exception {		roleMapper.updateRole(roleBean);	}	public List<RoleBean> listRoleByPage(Page<RoleBean> page) throws Exception {		return roleMapper.listRoleByPage(page);	}	public void removeRoleById(RoleBean roleBean) throws Exception {		roleMapper.removeRoleById(roleBean);	}}