package cn.com.bohui.bohuifin.service.role_priv;/** * 表名：t_role_priv */import javax.annotation.Resource;import org.springframework.stereotype.Service;import cn.com.bohui.bohuifin.mapper.RolePrivMapper;import java.util.List;import java.util.ArrayList;import cn.com.bohui.bohuifin.bean.Page;import cn.com.bohui.bohuifin.bean.RolePrivBean;@Servicepublic class RolePrivService {	@Resource	private RolePrivMapper rolePrivMapper;	public void saveRolePriv(RolePrivBean rolePrivBean) throws Exception {		rolePrivMapper.saveRolePriv(rolePrivBean);	}	public void updateRolePriv(RolePrivBean rolePrivBean) throws Exception {		rolePrivMapper.updateRolePriv(rolePrivBean);	}	public List<RolePrivBean> listRolePrivByPage(Page<RolePrivBean> page) throws Exception {		return rolePrivMapper.listRolePrivByPage(page);	}	public void removeRolePrivById(RolePrivBean rolePrivBean) throws Exception {		rolePrivMapper.removeRolePrivById(rolePrivBean);	}}