package cn.com.bohui.bohuifin.service.user_accounts;/** * 表名：t_user_accounts */import cn.com.bohui.bohuifin.bean.Page;import cn.com.bohui.bohuifin.bean.UserAccountsBean;import cn.com.bohui.bohuifin.mapper.UserAccountsMapper;import org.springframework.stereotype.Service;import javax.annotation.Resource;import java.util.List;@Servicepublic class UserAccountsService {    @Resource    private UserAccountsMapper userAccountsMapper;    public void saveUserAccounts(UserAccountsBean userAccountsBean) throws Exception {        userAccountsMapper.saveUserAccounts(userAccountsBean);    }    public void updateUserAccounts(UserAccountsBean userAccountsBean) throws Exception {        userAccountsMapper.updateUserAccounts(userAccountsBean);    }    public List<UserAccountsBean> listUserAccountsByPage(Page<UserAccountsBean> page) throws Exception {        return userAccountsMapper.listUserAccountsByPage(page);    }    public double findUserAccounts(String userId) throws Exception {        return userAccountsMapper.findUserAccounts(userId);    }    public void removeUserAccountsById(UserAccountsBean userAccountsBean) throws Exception {        userAccountsMapper.removeUserAccountsById(userAccountsBean);    }}