package cn.com.bohui.bohuifin.service.user_accounts;/** * 表名：t_user_accounts */import javax.annotation.Resource;import org.springframework.stereotype.Service;import cn.com.bohui.bohuifin.mapper.UserAccountsMapper;import java.util.List;import java.util.ArrayList;import cn.com.bohui.bohuifin.bean.Page;import cn.com.bohui.bohuifin.bean.UserAccountsBean;@Servicepublic class UserAccountsService {	@Resource	private UserAccountsMapper userAccountsMapper;	public void saveUserAccounts(UserAccountsBean userAccountsBean) throws Exception {		userAccountsMapper.saveUserAccounts(userAccountsBean);	}	public void updateUserAccounts(UserAccountsBean userAccountsBean) throws Exception {		userAccountsMapper.updateUserAccounts(userAccountsBean);	}	public List<UserAccountsBean> listUserAccountsByPage(Page<UserAccountsBean> page) throws Exception {		return userAccountsMapper.listUserAccountsByPage(page);	}	public void removeUserAccountsById(UserAccountsBean userAccountsBean) throws Exception {		userAccountsMapper.removeUserAccountsById(userAccountsBean);	}}