package cn.com.bohui.bohuifin.service.user_product_amount;/** * 表名：t_user_product_amount */import javax.annotation.Resource;import org.springframework.stereotype.Service;import cn.com.bohui.bohuifin.mapper.UserProductAmountMapper;import java.util.List;import java.util.ArrayList;import cn.com.bohui.bohuifin.bean.Page;import cn.com.bohui.bohuifin.bean.UserProductAmountBean;@Servicepublic class UserProductAmountService {	@Resource	private UserProductAmountMapper userProductAmountMapper;	public void saveUserProductAmount(UserProductAmountBean userProductAmountBean) throws Exception {		userProductAmountMapper.saveUserProductAmount(userProductAmountBean);	}	public void updateUserProductAmount(UserProductAmountBean userProductAmountBean) throws Exception {		userProductAmountMapper.updateUserProductAmount(userProductAmountBean);	}	public List<UserProductAmountBean> listUserProductAmountByPage(Page<UserProductAmountBean> page) throws Exception {		return userProductAmountMapper.listUserProductAmountByPage(page);	}	public void removeUserProductAmountById(UserProductAmountBean userProductAmountBean) throws Exception {		userProductAmountMapper.removeUserProductAmountById(userProductAmountBean);	}}