package cn.com.bohui.bohuifin.service.user_role;/** * 表名：t_user_role */import javax.annotation.Resource;import org.springframework.stereotype.Service;import cn.com.bohui.bohuifin.mapper.UserRoleMapper;import java.util.List;import java.util.ArrayList;import cn.com.bohui.bohuifin.bean.Page;import cn.com.bohui.bohuifin.bean.UserRoleBean;@Servicepublic class UserRoleService {	@Resource	private UserRoleMapper userRoleMapper;	public void saveUserRole(UserRoleBean userRoleBean) throws Exception {		userRoleMapper.saveUserRole(userRoleBean);	}	public void updateUserRole(UserRoleBean userRoleBean) throws Exception {		userRoleMapper.updateUserRole(userRoleBean);	}	public List<UserRoleBean> listUserRoleByPage(Page<UserRoleBean> page) throws Exception {		return userRoleMapper.listUserRoleByPage(page);	}	public void removeUserRoleById(UserRoleBean userRoleBean) throws Exception {		userRoleMapper.removeUserRoleById(userRoleBean);	}}