package cn.com.bohui.bohuifin.service.users;/** * 表名：t_users */import cn.com.bohui.bohuifin.bean.Page;import cn.com.bohui.bohuifin.bean.UsersBean;import cn.com.bohui.bohuifin.bean.vo.UsersVo;import cn.com.bohui.bohuifin.consts.ParamConst;import cn.com.bohui.bohuifin.consts.SystemConst;import cn.com.bohui.bohuifin.mapper.UsersMapper;import cn.com.bohui.bohuifin.util.LogicUtil;import org.springframework.stereotype.Service;import javax.annotation.Resource;import javax.servlet.http.HttpSession;import java.util.List;import java.util.Map;@Servicepublic class UsersService {    @Resource    private UsersMapper usersMapper;    public void saveUsers(UsersBean usersBean) throws Exception {        usersMapper.saveUsers(usersBean);    }    public void updatePwd(UsersBean usersBean) throws Exception {        usersMapper.updatePwd(usersBean);    }    public void updateUser(UsersBean usersBean) throws Exception {        usersMapper.updateUser(usersBean);    }    public List<UsersBean> listUsersByPage(Page<UsersBean> page) throws Exception {        return usersMapper.listUsersByPage(page);    }    public List<UsersBean> listAllUsers(Map<String, Object> params) throws Exception {        return usersMapper.listAllUsers(params);    }    public UsersBean findUserById(String userId) throws Exception {        return usersMapper.findUserById(userId);    }    public void removeUserById(UsersBean usersBean) throws Exception {        usersMapper.removeUserById(usersBean);    }    public void removeUsersById(UsersVo usersVo) throws Exception {        usersMapper.removeUsersById(usersVo);    }        public boolean isLogin(UsersBean usersBean, HttpSession session) throws Exception {        usersBean.setState(SystemConst.STATE_DEFAULT);        UsersBean login = usersMapper.getUsersBean(usersBean);        return LogicUtil.getInstance().checkIsLogin(login, usersBean, ParamConst.USER_LOGIN, session);    }    public boolean checkUserExist(String userId, String userName, int authorityGroupId) throws Exception {        UsersBean usersBean = new UsersBean();        usersBean.setUserId(userId);        usersBean.setUserName(userName);        usersBean.setAuthorityGroupId(authorityGroupId);        usersBean.setState(SystemConst.STATE_DEFAULT);        UsersBean result = usersMapper.getUsersBean(usersBean);        if (result != null) {            return true;        } else {            return false;        }    }}